,p_process_name=>'PreFill_Cost'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'begin',
'    with data_cost as',
'    (',
'        select ',
'            to_char(load_start_time,''DD-MON-YYYY HH24:MI'') cost_last_load_date,',
'            to_char(FILE_DATE,''DD-MON-YYYY HH24:MI'')       cost_max_file_date,',
'            round(sysdate-FILE_DATE,1)    cost_days_behind,',
'            batch_total-batch_id          cost_files_to_load,',
'            round((LOAD_END_TIME-LOAD_START_TIME)*24*60*60,1) cost_file_load_time',
'        from',
'        (',
'            select ',
'                a.*,',
'                ROW_NUMBER() OVER (order by load_start_time desc) rn ',
'            from oci_load_status a',
'            where file_type=''COST''',
'        ) where rn=1',
'    )',
'    select ',
'        cost_last_load_date,',
'        cost_max_file_date,',
'        cost_days_behind,',
'        case ',
'            when cost_files_to_load = 0 then to_char(cost_files_to_load) ',
'            else to_char(cost_files_to_load)||'' (''||round(cost_files_to_load*cost_file_load_time/60,1)||'' minutes)'' ',
'            end cost_files_to_load,',
'        cost_file_load_time',
'    into',
'        :p1_cost_last_load_date,',
'        :p1_cost_max_file_date,',
'        :p1_cost_days_behind,',
'        :p1_cost_files_to_load,',
'        :p1_cost_file_load_time',
'    from',
'        data_cost;',
'        ',
'    EXCEPTION',
'      WHEN NO_DATA_FOUND THEN',
'        return;',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>8463412871486801
);
