'    from',
'    (',
'        select /*+ parallel(oci_cost,8) full(oci_cost) */ ',
'            sum(COST_MY_COST) COST_MY_COST,',
'            sum(COST_MY_COST)*(24 * 365 / count(distinct USAGE_INTERVAL_START)) COST_YEAR,',
